export class AppToolbarController {
  constructor($location) {
    this.$location = $location;
  }

  $onInit() {
    var appId = this.getAppIdFromUrl();
    var protocol = this.$location.protocol();
    var host = this.$location.host();

    this.appDocUrl = protocol + "://" + host + "/marketplace/application/" + appId;
  }

  getAppIdFromUrl() {
    var appIdRegexp = /applications\/(\d+)\/(.*)/g;
    var matches = appIdRegexp.exec(this.$location.absUrl());
    return matches[1];
  }
}