import angular from 'angular';

export let apiService = angular
  .module('apiService', [])
  .factory('ApiService', ApiService)
  .name;

ApiService.$inject = ['$http'];

function ApiService($http) {

  var config = {
    endpoint: '/api/'
  };

  return {
    get: get,
    post: post,
    patch: patch,
    'delete': deleteApi,
  }

  function get(url) {
    return $http.get(config.endpoint + url);
  }

  function post(url, data) {
    return $http.post(config.endpoint + url, data);
  }

  function patch(url, data) {
    return $http.patch(config.endpoint + url, data);
  }

  function deleteApi(url) {
    return $http.delete(config.endpoint + url);
  }
}