import angular from 'angular';
import angularMaterial from 'angular-material';

export var angularMaterialConfig = angular
  .module('angularMaterialConfig', [
    angularMaterial
  ])
  .config(appConfig)
  .name;

appConfig.$inject = ['$mdThemingProvider'];

function appConfig($mdThemingProvider) {
  var white = $mdThemingProvider.extendPalette('blue-grey', {
    '50': 'FFFFFF',
    '800': 'FFFFFF',
    'contrastDefaultColor': 'dark'
  });

  $mdThemingProvider.definePalette('white', white);

  $mdThemingProvider.theme('default')
    .primaryPalette('blue-grey', {
      default: "800"
    })
    .accentPalette('red')
    .backgroundPalette('white');
}